package com.akirachix.mitushconnect

import android.os.Bundle
import androidx.activity.enableEdgeToEdge
import androidx.appcompat.app.AppCompatActivity
import androidx.core.view.ViewCompat
import androidx.core.view.WindowInsetsCompat
import androidx.recyclerview.widget.LinearLayoutManager
import com.akirachix.mitushconnect.databinding.ActivityPaymentBinding
import com.akirachix.mitushconnect.databinding.ActivityRecycleBinding

class PaymentsActivity : AppCompatActivity() {
    lateinit var binding:ActivityRecycleBinding
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        binding = ActivityRecycleBinding.inflate(layoutInflater)
        setContentView(binding.root)

        binding.rvRecycler.layoutManager = LinearLayoutManager(this)
        displayBlogs()
    }
    fun displayBlogs(){
        val blog1 = Payments("A bale of denim jeans","ksh 16,000","data:image/jpeg;base64,")
        val blog2 = Payments("A bale of denim jeans","Publishing date: 12-10-2023","data:image/jpeg;base64,")
        val blog4 = Payments("A bale of denim jeans","Publishing date: 08-06-2024","data:image/jpeg;base64,")
        val blog5 = Payments("A bale of denim jeans","Publishing date: 08-06-2024","data:image/jpeg;base64,")
        val myblogs = listOf(blog1,blog2,blog4,blog5)


        val blogsAdapter = PaymentsAdapter(myblogs)
        binding.rvRecycler.adapter= blogsAdapter


    }
}

